sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if ( newValue === '') {
		return;
	}

	var whatneeded = g_form.getValue('u_what_needed');
// 	alert(whatneeded);

	// clear all of the default choices from the What needed field choice list
	g_form.clearOptions('u_what_needed');


	if (newValue == 'hr') {
		g_form.addOption('u_what_needed', 'hr1', 'hr1 is here..');
		g_form.addOption('u_what_needed', 'hr2', 'hr 2 here');
// 		g_form.addInfoMessage('Adding custom values to WHAT NEEDED field');

	}

	if(isLoading && !g_form.isNewRecord() ) {
		g_form.setValue('u_what_needed', whatneeded);
	}

}
